helper-create-win10-vm-debug() { echo "=== DEBUG: All Set VM_* Variables ==="; declare -A normal_vars array_vars array_lengths; for var in $(compgen -v | grep '^VM_'); do value="${!var}"; if [[ "$(declare -p $var 2>/dev/null)" =~ "declare -a" ]]; then eval "filtered_array=(\"\${$var[@]}\")"; filtered_array=("${filtered_array[@]/()/}"); array_vars[$var]="${filtered_array[*]}"; array_lengths[$var]=${#filtered_array[@]}; else normal_vars[$var]="$value"; fi; done; for var in "${!normal_vars[@]}"; do printf '%s="%s"\n' "$var" "${normal_vars[$var]}"; done; echo ""; for var in "${!array_vars[@]}"; do printf '%s=(%s)\n' "$var" "${array_vars[$var]}"; done; echo ""; for var in "${!array_lengths[@]}"; do printf '%s length: %d\n' "$var" "${array_lengths[$var]}"; done; echo ""; }