helper-repack-iso-create-iso-with-xorriso() { local src_folder="$1" out_iso="$2" efi_boot="efi/microsoft/boot/efisys$([[ $autoboot_flag -eq 1 ]] && echo '_noprompt').bin"; xorriso -as mkisofs -iso-level 3 -full-iso9660-filenames -joliet -joliet-long -volid "CCCOMA_X64FRE_EN-GB_DV9" -output "$out_iso" -eltorito-boot boot/etfsboot.com -eltorito-catalog boot/boot.cat -no-emul-boot -boot-load-size 8 -boot-info-table -eltorito-alt-boot -e "$efi_boot" -no-emul-boot -boot-load-size 1 -isohybrid-gpt-basdat "$src_folder"; }