helper-repack-iso-get-sourceiso() { [[ "$1" == //* ]] && { [[ "$2" == *"username"* ]] && sourceiso="$(get-iso-file-path "$1" "$2")" || sourceiso="$(get-iso-file-path "$1")"; [[ $? -ne 0 ]] && { echo "Error: Source ISO not found."; return 1; }; shift $([[ "$2" == *"username"* ]] && echo 2 || echo 1); } || { sourceiso="$(get-iso-file-path "$1")" || { echo "Error: Source ISO not found."; return 1; }; shift 1; }; }