windows-setup-runsynccommands() { WINDOWS_SHELL_SETUP_ORDER=1; local args=() has_commands=false index=0; while [[ $# -gt 0 ]]; do if [[ "$1" == "addcommand" && ${#args[@]} -gt 0 ]]; then [[ "$has_commands" == false ]] && echo -e "\t<RunSynchronous>" && has_commands=true; windows-setup-runsynccommand "${args[@]}"; args=(); else args+=("$1"); fi; shift; done; if [[ ${#args[@]} -gt 0 ]]; then [[ "$has_commands" == false ]] && echo -e "\t<RunSynchronous>" && has_commands=true; windows-setup-runsynccommand "${args[@]}"; fi; if [[ -n "${runsynccommandarr_ubound}" && "${runsynccommandarr_0_path+set}" ]]; then [[ "$has_commands" == false ]] && echo -e "\t<RunSynchronous>" && has_commands=true; for ((index = 0; index < runsynccommandarr_ubound; index++)); do eval "windows-setup-runsynccommand \"\$index\""; done; fi; [[ "$has_commands" == true ]] && echo -e "\t</RunSynchronous>"; }