windows-shell-setup-add-firstlogoncommands-addtask(){ [[ -z "$1" || -z "$2" ]] && { echo "Usage: $FUNCNAME <command> <description>"; return 1; }; firstcommandarr_ubound=${firstcommandarr_ubound:-0}; local index=$firstcommandarr_ubound; eval "firstcommandarr_${index}_commandline='cmd /c schtasks /create /tn \"$2\" /tr \"cmd /c '$1'\" /sc ONLOGON /ru \"SYSTEM\"'"; eval "firstcommandarr_${index}_description='$2'"; eval "firstcommandarr_${index}_requiresinput='false'"; firstcommandarr_ubound=$((index + 1)); }