windows-shell-setup-add-firstlogoncommands(){ firstcommandarr_ubound=${firstcommandarr_ubound:-0}; local index=$firstcommandarr_ubound; eval "firstcommandarr_${index}_commandline='$1'"; eval "firstcommandarr_${index}_description='${2:-command $index}'"; eval "firstcommandarr_${index}_requiresinput='${3:-false}'"; [[ -n "$4" ]] && eval "firstcommandarr_${index}_order='$4'"; firstcommandarr_ubound=$((index + 1)); }