windows-security-spp() { local arch="amd64" skip_rearm="1"; [[ "$1" == "x86" ]] && { arch="x86"; shift; }; while [[ $# -gt 0 ]]; do case "$1" in SkipRearm) shift; skip_rearm="$1" ;; esac; shift; done; [[ "$skip_rearm" == "" ]] && return; echo -e "\t\t<component name=\"Microsoft-Windows-Security-SPP\" processorArchitecture=\"$arch\" publicKeyToken=\"31bf3856ad364e35\" language=\"neutral\" versionScope=\"nonSxS\" xmlns:wcm=\"http://schemas.microsoft.com/WMIConfig/2002/State\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\">\n\t\t\t<SkipRearm>$skip_rearm</SkipRearm>\n\t\t</component>"; }