windows-setup-disk-configuration-createpartition() { local extend="true" order="1" size="" type="Primary"; while [[ $# -gt 0 ]]; do case "$1" in Extend) shift; extend="$1" ;; Order) shift; order="$1" ;; Size) shift; size="$1"; extend="" ;; Type) shift; type="$1" ;; esac; shift; done; echo -e "\t\t\t\t\t\t<CreatePartition wcm:action=\"add\">\n\t\t\t\t\t\t\t<Order>$order</Order>\n\t\t\t\t\t\t\t<Type>$type</Type>"; [[ -n "$size" ]] && echo -e "\t\t\t\t\t\t\t<Size>$size</Size>"; [[ -n "$extend" ]] && echo -e "\t\t\t\t\t\t\t<Extend>$extend</Extend>"; echo -e "\t\t\t\t\t\t</CreatePartition>"; }