windows-setup-userdata() { local AcceptEula="true" FullName="user" Organization="" Key="W269N-WFGWX-YVC9B-4J6C9-T83GX" WillShowUI="Never"; while [[ $# -gt 0 ]]; do case "$1" in AcceptEula|FullName|Organization|Key|WillShowUI) eval "$1=\$2"; shift 2 ;; *) echo "Unknown parameter: $1" >&2; return 1 ;; esac; done; echo -e "\t\t\t<UserData>\n\t\t\t\t<ProductKey>\n\t\t\t\t\t<Key>$Key</Key>"; [[ -n "$WillShowUI" ]] && echo -e "\t\t\t\t\t<WillShowUI>$WillShowUI</WillShowUI>"; echo -e "\t\t\t\t</ProductKey>\n\t\t\t\t<AcceptEula>$AcceptEula</AcceptEula>\n\t\t\t\t<FullName>$FullName</FullName>\n\t\t\t\t<Organization>$Organization</Organization>\n\t\t\t</UserData>"; }