windows-shell-setup-autologon() { local username="" password="" plaintext="true" enabled="true" domain="" logon_count=""; while [[ $# -gt 0 ]]; do case "$1" in Username) shift; username="$1" ;; Password) shift; password="$1" ;; PlainText) shift; plaintext="$1" ;; Enabled) shift; enabled="$1" ;; Domain) shift; domain="$1" ;; LogonCount) shift; logon_count="$1" ;; esac; shift; done; [[ -z "$username" ]] && return; echo -e "\t\t<AutoLogon>"; [[ -n "$password" ]] && echo -e "\t\t\t<Password>\n\t\t\t\t<Value>$password</Value>\n\t\t\t\t<PlainText>$plaintext</PlainText>\n\t\t\t</Password>"; [[ -n "$domain" ]] && echo -e "\t\t\t<Domain>$domain</Domain>"; echo -e "\t\t\t<Enabled>$enabled</Enabled>"; [[ -n "$logon_count" ]] && echo -e "\t\t\t<LogonCount>$logon_count</LogonCount>"; echo -e "\t\t\t<Username>$username</Username>\n\t\t</AutoLogon>"; }