windows-shell-setup-localaccounts() { local args=() has_users=false; echo -e "\t\t\t<LocalAccounts>"; while [[ $# -gt 0 ]]; do if [[ "$1" == "adduser" ]]; then [[ ${#args[@]} -gt 0 ]] && { windows-shell-setup-localaccount "${args[@]}"; args=(); has_users=true; }; else args+=("$1"); fi; shift; done; [[ ${#args[@]} -gt 0 ]] && { windows-shell-setup-localaccount "${args[@]}"; has_users=true; }; echo -e "\t\t\t</LocalAccounts>"; }