windows-sqmapi() { local arch="amd64" ceip_enabled="0"; [[ "$1" == "x86" ]] && { arch="x86"; shift; }; while [[ $# -gt 0 ]]; do case "$1" in CEIPEnabled) shift; ceip_enabled="$1" ;; esac; shift; done; [[ "$ceip_enabled" == "" ]] && return; echo -e "\t\t<component name=\"Microsoft-Windows-SQMApi\" processorArchitecture=\"$arch\" publicKeyToken=\"31bf3856ad364e35\" language=\"neutral\" versionScope=\"nonSxS\" xmlns:wcm=\"http://schemas.microsoft.com/WMIConfig/2002/State\" xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\">\n\t\t\t<CEIPEnabled>$ceip_enabled</CEIPEnabled>\n\t\t</component>"; }